const roles = ["", ""]; //İstediğiniz Rolü Yazabilirsiniz
const reactions = ['', '']; //İstediğiniz Emojiyi Ekleyebilirsiniz.Eğer Hareketli Eklemek İstiyorsanız Emoji İd
const botToken = "";  //Buraya Bot Token        
